mapper.select:
select

from show_comment
    JOIN user_info ON show_comment.user_id=user_info.id
    LEFT JOIN show_comment parent_show_comment ON show_comment.parent_comment_id=parent_show_comment.id
    LEFT JOIN user_info parent_user_info ON parent_show_comment.user_id=parent_user_info.id




  order by ${orderByClause}



    limit ${offset}, ${rows}


    limit ${rows}
